pragma solidity 0.8.27;

// We put the definitions outside so they can be imported to different contracts (in this case VaultCore and VaultInspector)
    enum StrategyType {
        Generalized4626,
        Ethena,
        Other
    }
    struct AssetInfo {
        address ltToken;
        uint32 weight;
        uint8 decimals;
        uint8 index;
        StrategyType strategyType;
        address strategy;
        uint256 minimalAmountInVault;
    }","versionPragma":"0.8.27"},"contracts/utils/Errors.sol":{"content":"pragma solidity 0.8.27;

contract Errors {
    error Unauthorized();
    error ZeroAddress();
    error ZeroAmount();
    error Inconsistency();
    error DefaultValueRequired();
    error NotSupported();
    error StalePrice();
    error SlippageTooHigh();
    error InsufficientBalance();
    error RouterNotWhitelisted();
}
